// Bot: pinpoint the peices with the necrotic Mace 
// Author: bogalj
using System;

using RBot;
using RBot.Servers;

public class Script
{
    public void ScriptMain(ScriptInterface bot)
    {
        bot.Options.SafeTimings = true;
        bot.Options.RestPackets = true;
        bot.Options.AutoRelogin = true;
        bot.Options.ExitCombatBeforeQuest = true;
        bot.Options.InfiniteRange = true;
        bot.Options.SkipCutscenes = true;

        
       bot.Skills.Add( 1, 1 );
		bot.Skills.Add( 2, 1 );
		bot.Skills.Add( 3, 1 );
		bot.Skills.Add( 4, 1 );
		bot.Skills.StartTimer();
		bot.Drops.Add("Ominous Aura");
		bot.Drops.Add("Dark Spirit Orb");
		bot.Drops.Add("Doom Aura");
		bot.Drops.Add("Dark Energy");
		bot.Drops.Enabled = true;
       
        bot.Player.Join("dwarfhold-999999");
    	
    	while(!bot.ShouldExit()) {
    	bot.Player.RejectExcept("Dark Energy, Ominous Aura, Dark Spirit Orb, Doom Aura");
                bot.Player.Pickup("Dark Energy, Ominous Aura, Dark Spirit Orb, Doom Aura");
        bot.Player.Jump("r2", "Left", false);
        bot.Quests.EnsureAccept(2185);  //change quest id here depending
        //Quest Ids Below, edit here^
        //----------------
        //bow- 2186
        //mace- 2185
        //scythe- 2184
        //broadsword- 2183
        //shade blade- 2182
        //daggers- 2181
        
        bot.Player.HuntForItem("Chaos Drow", "DoomKnight Armor Piece",10, true, true);
        bot.Player.RejectExcept("Dark Energy");
        bot.Player.Jump("Blank", "Spawn", false);
        bot.Quests.EnsureComplete(2185);   //change quest id here depending
        bot.Wait.ForDrop("Dark Energy, Ominous Aura, Dark Spirit Orb, Doom Aura");
        bot.Player.Pickup("Dark Energy, Ominous Aura, Dark Spirit Orb, Doom Aura");

        }
    }
}
